css:
/*pc*/@media (min-width: 601px){
  h1{font-size:20px; color: #192052;}
  h5{font-size: 14px;}
  .header__logo {
    max-height: 90px;
    max-width: 300px;
}
  .html-clearfix{color:#000;font-size:18px;}



  .prices__value{font-size:25px; color: #192052; font-weight:700;}
  .header__search-btn {
    font-size: 1.2rem;}
  .i-shopping-bag{color: #192051;}
  .header__search-btn{color: #192051;}
  .header__buttons .btn {color: #1a2154;}
  .badge-primary {
    background-color: #b7bcdd;
    color: #192052;
}
  .header__nav a:hover {color:#192052;background: transparent;font-size: 17px;
    font-weight:700; text-decoration:none;margin-bottom: 0px;
    border-bottom: 0px solid #192052;border-radius: 0px;}
.header__search-input {
    border-radius: 10px;
  border: 2px solid #d9d9d9;}
  #search-bar {margin-top: -30px;
    margin-left: 0px;
    width: 400px;}
  .header__nav {
   font-size: 17px;
    margin-top: -28px;
    margin-left: 160px;
    line-height: var(--line-height-lg);
    text-align: center;
    text-transform: none;}


  .top-bar {
    background: #191d47;
    width: 100%;
}
  .top-bar__nav {
    color: #fff;}
.d-lg-inline {
    display: none!important;
}
  .header {
    background: #ffffff;
    border-bottom: 1px solid #e7e7e7;
    box-shadow: -6px 15px 14px -9px rgba(0,0,0,0.37);}}

 /*mobile*/@media (max-width: 600px) {
   .top-bar {
    display:none;

}}

 /*responsivo*/
.col-12.col-xl.order-lg-first p {
    color: #fff;
  font-size: 16px;}
.btn-primary {
    background-color: #ffffff;
    border-color: #192052;
    color: #192052;border-radius: 30px;
}
.btn-primary:hover {
    background-color: #b7b9e3;
    border-color: #b7b9e3;
    color: #192052;border-radius: 30px;}
.btn-outline-secondary {
    border-color: #192052;background-color:#192052;
    color: #fff;border-radius: 30px;
}
.btn-outline-secondary:hover {border-radius: 30px;}
.btn-success{background-color: #ffffff;
    border-color: #192052;
    color: #192052;border-radius: 30px;
}
.btn-success:hover {background-color: #b7b9e3;
    border-color: #b7b9e3;
    color: #192052;border-radius: 30px;
}
.btn-light {
    background-color: #4f568d;
    border-color: #f8f9fa;
    color: #ffffff;
    padding: 20px 20px 20px 20px;
    border-radius: 30px;
}
.product-card__name{font-size:16px;color:#000;text-transform: none;}
.menu {
    background: #2c3364;}
  .menu__item {
    align-items: center;
    border-bottom: var(--border-width) solid #2c3364;
    color: #ffffff;}
  .menu__phone{color:#fff;}
  .menu__social{color:#fff;}
  .menu__link {
    border-top: var(--border-width) dashed var(--primary-lightest-yiq);
    color: #ffffff;}
.menu__link a:hover {color:#fff; text-transform: none;background: #192052;}
.i-heart {color:#192052; }
.menu__btn {
    color: #ffffff;}
.footer {
   background: #192052; 
  box-shadow: -6px 15px 14px -9px rgba(0,0,0,0.37);}
.footer__title{color:#fff;}
.footer__links i {
    color: #192052;
}
.footer__social{color:#fff !important;}
.footer__contacts{color:#fff;}
.footer__credits {
    align-items: center;
  border-top: var(--border-width) solid #333b74;color:#fff;}
  .footer__store {
    color: #ffffff;
    font-weight: 700;
}
 .list-unstyled { color:#fff;font-size: 16px;font-weight: 400;}
.footer__list-icon{color:#fff !important;}
.ul.footer__links{color:#fff;!important;}
.footer__links a {color:#fff;!important;}
.footer__links a:hover {color:#fff;!important; text-decoration:none;}
.footer__contacts{color:#fff;!important;}
.product__name{font-weight:700;!important;}
.col-12.col-sm-6.col-lg.col-xl-auto.px-xl-4{color:#fff !important;}
.col-12.col-lg-auto.order-lg-last.pl-xl-4{color:#fff !important;}
.whatsapp-link {color:#fff;!important;}
.whatsapp-link a {color:#fff;!important;}
.whatsapp-link a:hover {color:#fff;!important;}
.i-facebook{color:#fff;}
.i-instagram{color:#fff;}
.i-tiktok{color:#fff;}
.product__short-stock{display:none;}
 .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
.menu__footer a {
    color: #ffffff;
    text-decoration: none;
}
.product-card__pictures {
    height: 300px;
}
.cart__info {
    background: #f5f5f5;
    border-radius: 10px;
    padding: 1rem;
}
.cart .cart-item {
    border-top: 1px dotted #c5ccd3;
    padding-top: 1rem;
}
.cart-item__name {color:#000;
  font-size: 16px;}
.app__footer {
    background: linear-gradient(180deg,var(--light) 0,var(--body-bg) 90%);
    border-top: var(--spacer-1) solid #e5e5e5;
}
.span.mr-2{display:none;}
.shipping-line[data-service-code="PROPRIA"] span {
  display: none
}
.order-info__shipping-freight .shipping-line span {
  display: none
}
#ws_icon_chat img {
    width: 80px !important;
}
#ws_icon_chat {
    position: fixed;
    bottom: 10px;
    left: 15px;
    z-index: 2000;
    cursor: pointer;
    outline: none !important;
}
.__pages_contato{background-color:#192052;}
.__pages_sobre-nos{background-color:#192052;}
.retail-grid {background-color: #fff;}
.pb-lg-4{padding-bottom: 0px;}

.app__header {
  background: #ffffff;box-shadow: -6px 15px 14px -9px rgba(0,0,0,0.37);}
}
