css:
/*pc*/@media (min-width: 601px){
h1{font-size:20px; color: #192052;}
h5{font-size: 14px;}
.header__logo {max-height: 90px;max-width: 300px;}
.html-clearfix{color:#000;font-size:18px;}
#search-bar {margin-top: -130px;margin-left: 640px;width: 100px;}
.prices__value{font-size:25px; color: #192052; font-weight:700;}
.header__search-btn {font-size: 1.2rem;}

.header__search-btn{color: #192051;}
.header__buttons .btn {color: #fcfcfc;}
.badge-primary {background-color: #b7bcdd;color: #192052;}
.header__nav a:hover {color:#fff;background: transparent;font-size: 16px;
font-weight:700; text-decoration:none;margin-bottom: 0px;border-bottom: 0px solid #192052;
border-radius: 0px;}
.header__search-input {border-radius: 20px;border: 2px solid #d9d9d9;width: 300px;}
.header__nav a {color:#fff;font-size: 16px;font-weight: 500;}
 .header__nav  {color:#fff;font-size: 16px;font-weight: 500;margin-top: -48px;margin-left: 100px;
line-height: var(--line-height-lg);text-align: center;text-transform: none;}
.top-bar {background:background: rgb(25,33,80);
background: -moz-linear-gradient(90deg, rgba(25,33,80,1) 0%, rgba(69,83,167,1) 35%, rgba(72,84,149,1) 100%);
background: -webkit-linear-gradient(90deg, rgba(25,33,80,1) 0%, rgba(69,83,167,1) 35%, rgba(72,84,149,1) 100%);
