filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#192150",endColorstr="#485495",GradientType=1);
    width: 100%;}
.top-bar__nav {font-size:14px;color: #fff;}
.d-lg-inline {display: none!important;}
.header {background: background: rgb(25,33,80);
background: -moz-linear-gradient(90deg, rgba(25,33,80,1) 0%, rgba(69,83,167,1) 35%, rgba(72,84,149,1) 100%);
