filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#192150",endColorstr="#485495",GradientType=1);
    border-bottom: 1px solid #e7e7e7;
    box-shadow: -6px 15px 14px -9px rgba(0,0,0,0.37);
    top: -12px;}
}

 /*mobile*/@media (max-width: 600px) {
   .header__search-btn{color: #fff;}
   .top-bar {
     display:none;}
     .header{background: background: rgb(25,33,80);
background: -moz-linear-gradient(90deg, rgba(25,33,80,1) 0%, rgba(69,83,167,1) 35%, rgba(72,84,149,1) 100%);
background: -webkit-linear-gradient(90deg, rgba(25,33,80,1) 0%, rgba(69,83,167,1) 35%, rgba(72,84,149,1) 100%);
background: linear-gradient(90deg, rgba(25,33,80,1) 0%, rgba(69,83,167,1) 35%, rgba(72,84,149,1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#192150",endColorstr="#485495",GradientType=1);
    border-bottom: 1px solid #e7e7e7;
    box-shadow: -6px 15px 14px -9px rgba(0,0,0,0.37);

}
    .header__buttons .btn {
        color: #fcfcfc;
    }

}

 /*responsivo*/
.i-shopping-bag{color: #fcfcfc;}
.col-12.col-xl.order-lg-first p {
    color: #fff;
  font-size: 16px;}
.btn-primary {
    background-color: #ffffff;
    border-color: #192052;
    color: #192052;border-radius: 30px;
}
.btn-primary:hover {
    background-color: #b7b9e3;
    border-color: #b7b9e3;
    color: #192052;border-radius: 30px;}
.btn-outline-secondary {
    border-color: #192052;background-color:#192052;
    color: #fff;border-radius: 30px;
}
.btn-outline-secondary:hover {border-radius: 30px;}
.btn-success{background-color: #ffffff;
    border-color: #192052;
    color: #192052;border-radius: 30px;
}
.btn-success:hover {background-color: #b7b9e3;
    border-color: #b7b9e3;
    color: #192052;border-radius: 30px;
}
.btn-light {
    background-color: #b9bedf;
    border-color: #f8f9fa;
    color: #ffffff;
    padding: 20px 20px 20px 20px;
    border-radius: 30px;
}
.product-card__name{font-size:16px;color:#000;text-transform: none;}
.menu {
    background: background: rgb(25,33,80);
background: -moz-linear-gradient(90deg, rgba(25,33,80,1) 0%, rgba(69,83,167,1) 35%, rgba(72,84,149,1) 100%);
