    color: #ffffff;}
.menu__item:hover {
    background: #aaaecf;
    text-decoration: none;
}
  .menu__phone{color:#fff;}
  .menu__social{color:#fff;}
  .menu__link {
    border-top: var(--border-width) dashed var(--primary-lightest-yiq);
    color: #ffffff;}
.menu__link a:hover {color:#fff; text-transform: none;
  background: #8788ab;}
.i-heart {color:#192052; }
.menu__btn {
    color: #ffffff;}
.footer {
   background: background: rgb(25,33,80);
