    color: #fff;
}
  .footer__store {
    color: #ffffff;
    font-weight: 700;
}
 .list-unstyled { color:#fff;font-size: 16px;font-weight: 400;}
.footer__list-icon{color:#fff !important;}
.ul.footer__links{color:#fff;!important;}
