  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
.menu__footer a {color: #ffffff;text-decoration: none;}
.product-card__pictures {height: 300px;}
.product-card__offer-stamp {background-color: #293cc2;}
.cart__info {background: #f5f5f5;border-radius: 10px;padding: 1rem;}
