.__pages_contato{background-color:#192052;}
.__pages_sobre-nos{background-color:#192052;}
.retail-grid {background-color: #fff;}
.pb-lg-4{padding-bottom: 0px;}
.bt {background: #28a745;padding: 30px 30px 30px 30px;margin: 50px 50px 50px 50px;
    border-radius: 0px;color: #fff;font-size: 25px;}
.search-engine__toggles {color: #e8e8f3;margin-left: var(--spacer-1);}
.info-bar {
  background: #4f5793;}
